new Vue({el:"#app",data:{userSearch:""},methods:{getJson(e){return fetch(e).then((e=>e.json())).catch((e=>{this.$refs.error.text=e}))},postJson(e,t){return fetch(e,{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(t)}).then((e=>e.json())).catch((e=>{this.$refs.error.text=e}))},putJson(e,t){return fetch(e,{method:"PUT",headers:{"Content-Type":"application/json"},body:JSON.stringify(t)}).then((e=>e.json())).catch((e=>this.$refs.error.text=e))},delJson(e,t){return fetch(e,{method:"DELETE",headers:{"Content-Type":"application/json"},body:JSON.stringify(t)}).then((e=>e.json())).catch((e=>this.$refs.error.setText(e)))}},mounted(){}});